problemWECorrect.correctSolutionProposal:
public class ConversaoTemperatura {
    public static void main(String[] args) {
        double temperatura = 25.0;
        String unidade = "Celsius";
        double resultado = converterTemperatura(temperatura, unidade);
        System.out.println("Temperatura convertida: " + resultado);
    }

    public static double converterTemperatura(double temperatura, String unidade) {
        if (unidade.equalsIgnoreCase("Celsius")) {
            return (temperatura * 9 / 5) + 32;
        } else if (unidade.equalsIgnoreCase("Fahrenheit")) {
            return (temperatura - 32) * 5 / 9;
        } else {
            System.out.println("Unidade de temperatura inválida.");
            return 0.0;
        }
    }
}

problemWEIncorrect.incorrectSolution:
public class ConversaoTemperaturaIncorreto {
    public static void main(String[] args) {
        double temperatura = 25.0;
        String unidade = "Celsius";
        double resultado = converterTemperatura(temperatura, unidade);
        System.out.println("Temperatura convertida: " + resultado);
    }

    public static double converterTemperatura(double temperatura, String unidade) {
        if (unidade.equalsIgnoreCase("Celsius")) {
            return (temperatura - 32) * 5 / 9;
        } else if (unidade.equalsIgnoreCase("Fahrenheit")) {
            return (temperatura * 9 / 5) + 32;
        } else {
            System.out.println("Unidade de temperatura inválida.");
            return 0.0;
        }
    }
}
