problemWECorrect.correctSolutionProposal:
public class CalculoPotencia {
    public static void main(String[] args) {
        int base = 2;
        int expoente = 3;
        double resultado = calcularPotencia(base, expoente);
        System.out.println("Resultado da potência: " + resultado);
    }

    public static double calcularPotencia(int base, int expoente) {
        if (expoente < 0) {
            System.out.println("Expoente deve ser não negativo.");
            return -1;
        }
        double resultado = 1.0;
        for (int i = 0; i < expoente; i++) {
            resultado *= base;
        }
        return resultado;
    }
}

