problemWECorrect.correctSolutionProposal:
public class EncontrarMaximoMinimo {
    public static void main(String[] args) {
        double[][] matriz = {{3.0, 5.5, 7.8}, {2.0, 4.5, 6.0}};
        double[] resultado = encontrarMaximoMinimo(matriz);
        System.out.println("Máximo: " + resultado[0] + ", Mínimo: " + resultado[1]);
    }

    public static double[] encontrarMaximoMinimo(double[][] matriz) {
        if (matriz == null || matriz.length == 0 || matriz[0].length == 0) {
            return new double[]{0.0, 0.0};
        }
        double maximo = matriz[0][0];
        double minimo = matriz[0][0];
        for (int i = 0; i < matriz.length; i++) {
            for (int j = 0; j < matriz[0].length; j++) {
                double elemento = matriz[i][j];
                if (elemento > maximo) {
                    maximo = elemento;
                }
                if (elemento < minimo) {
                    minimo = elemento;
                }
            }
        }
        return new double[]{maximo, minimo};
    }
}

