problemWECorrect.correctSolutionProposal:
#include <stdio.h>

int calcularFatorial(int numero) {
    int fatorial = 1;
    for (int i = 1; i <= numero; i++) {
        fatorial *= i;
    }
    return fatorial;
}

int main() {
    int resultado = calcularFatorial(5);
    printf("Resultado do fatorial: %d\n", resultado);
    return 0;
}

