problemWECorrect.correctSolutionProposal:
#include <stdio.h>

double calcularPorcentagem(double valor_total, double porcentagem) {
    return (valor_total * porcentagem) / 100;
}

int main() {
    double resultado = calcularPorcentagem(200, 15);
    printf("Resultado do cálculo de porcentagem: %.2f\n", resultado);

    return 0;
}

