problemWECorrect.correctSolutionProposal:
#include <stdio.h>

double calcularIMCIncorreto(double peso, double altura) {
    return peso / (altura * altura);
}

int main() {
    double resultado = calcularIMCIncorreto(70, 1.75);
    printf("IMC calculado: %.2f\n", resultado);

    return 0;
}

