problemWECorrect.correctSolutionProposal:
#include <stdio.h>

double converterParaCotacao(double valor, double cotacao) {
    return valor * cotacao;
}

int main() {
    double resultado = converterParaCotacao(100, 5);
    printf("Resultado da conversão: %.2f\n", resultado);

    return 0;
}

problemWEIncorrect.incorrectSolution:
#include <stdio.h>

double converterParaCotacaoIncorreto(double valor, double cotacao) {
    return valor + cotacao;
}

int main() {
    double resultado = converterParaCotacaoIncorreto(100, 5);
    printf("Resultado da conversão: %.2f\n", resultado);

    return 0;
}
