problemWECorrect.correctSolutionProposal:
notas = [7.5, 6.0, 8.3, 5.5, 9.2]
media = sum(notas) / len(notas)
print(f'Média das notas: {media}')
if media >= 7.0:
    print('Situação: Aprovado')
elif media >= 5.0:
    print('Situação: Recuperação')
else:
    print('Situação: Reprovado')

problemWEIncorrect.incorrectSolution:
notas = [7.5, 6.0, 8.3, 5.5, 9.2]
media = 0.0
for i in range(1, len(notas)):
    media += notas[i]
media /= len(notas)
print(f'Média das notas: {media}')
if media >= 7.0:
    print('Situação: Aprovado')
elif media >= 5.0:
    print('Situação: Recuperação')
else:
    print('Situação: Reprovado')
