problemWECorrect.correctSolutionProposal:
def matrizes_multiplicaveis(matrizA, matrizB):
    return len(matrizA[0]) == len(matrizB)


def multiplicar_matrizes(matrizA, matrizB):
    linhasA, colunasA = len(matrizA), len(matrizA[0])
    colunasB = len(matrizB[0])

    resultado = [[0] * colunasB for _ in range(linhasA)]

    for i in range(linhasA):
        for j in range(colunasB):
            for k in range(colunasA):
                resultado[i][j] += matrizA[i][k] * matrizB[k][j]

    return resultado


def imprimir_matriz(matriz):
    for linha in matriz:
        print(linha)


matrizA = [[1, 2, 3], [4, 5, 6]]
matrizB = [[7, 8], [9, 10], [11, 12]]

if matrizes_multiplicaveis(matrizA, matrizB):
    resultado = multiplicar_matrizes(matrizA, matrizB)
    imprimir_matriz(resultado)
else:
    print('Matrizes não são multiplicáveis.')

