public class NumerosIncorreto {
    public static void main(String[] args) {
        int[] numeros = new int[10];

        for (int i = 1; i <= 10; i++) {
            numeros[i] = i;
        }

        for (int numero : numeros) {
            System.out.println(numero);
        }
    }
}

