problemWECorrect.correctSolutionProposal:
#include <stdio.h>

int main() {
    double notas[] = {7.5, 6.0, 8.3, 5.5, 9.2};
    double media = 0.0;

    for (int i = 0; i < sizeof(notas) / sizeof(notas[0]); i++) {
        media += notas[i];
    }

    media /= sizeof(notas) / sizeof(notas[0]);

    printf("Média das notas: %.2f\n", media);

    if (media >= 7.0) {
        printf("Situação: Aprovado\n");
    } else if (media >= 5.0) {
        printf("Situação: Recuperação\n");
    } else {
        printf("Situação: Reprovado\n");
    }

    return 0;
}

