#include <stdio.h>

void multiplicarMatrizes(int matrizA[][3], int matrizB[][2], int resultado[][2]) {
    for (int i = 0; i < 2; ++i) {
        for (int j = 0; j < 2; ++j) {
            resultado[i][j] = 0;
            for (int k = 0; k < 3; ++k) {
                resultado[i][j] += matrizA[i][k] * matrizB[k][j];
            }
        }
    }
}

void imprimirMatriz(int matriz[][2]) {
    for (int i = 0; i < 2; ++i) {
        for (int j = 0; j < 2; ++j) {
            printf("%d ", matriz[i][j]);
        }
        printf("\n");
    }
}

int main() {
    int matrizA[2][3] = {{1, 2, 3}, {4, 5, 6}};
    int matrizB[3][2] = {{7, 8}, {9, 10}, {11, 12}};
    int resultado[2][2];

    multiplicarMatrizes(matrizA, matrizB, resultado);
    imprimirMatriz(resultado);

    return 0;
}

