int main() {
    int numeros[10];
    int i, j;

    for (i = 1; i <= 10; i++) {
        numeros[i - 1] = i;
    }

    for (j = 0; j < 10; j++) {
        printf("%d\n", numeros[j]);
    }

    return 0;
}

