#include <stdio.h>
#include <stdlib.h>
#include <time.h>

int main() {
    // Seed para a função rand()
    srand(time(NULL));

    // Criar um array de inteiros com tamanho 5
    int arrayAleatorio[5];

    // Preencher o array com números aleatórios entre 1 e 100
    for (int i = 0; i < sizeof(arrayAleatorio) / sizeof(arrayAleatorio[0]); i++) {
        arrayAleatorio[i] = rand() % 100 + 1;
    }

    // Imprimir o array de números aleatórios
    for (int i = 0; i < sizeof(arrayAleatorio) / sizeof(arrayAleatorio[0]); i++) {
        printf("%d ", arrayAleatorio[i]);
    }

    return 0;
}

