problemWECorrect.correctSolutionProposal:
#include <stdio.h>

int main() {
    int array1[] = {1, 2, 3, 4, 5};
    int array2[] = {4, 5, 6, 7, 8};

    int tamanhoIntersecao = 0;

    // Encontrar tamanho da interseção
    for (int i = 0; i < sizeof(array1) / sizeof(array1[0]); i++) {
        for (int j = 0; j < sizeof(array2) / sizeof(array2[0]); j++) {
            if (array1[i] == array2[j]) {
                tamanhoIntersecao++;
                break;
            }
        }
    }

    int intersecao[tamanhoIntersecao];
    int contadorArray = 0;

    // Preencher a interseção
    for (int i = 0; i < sizeof(array1) / sizeof(array1[0]); i++) {
        for (int j = 0; j < sizeof(array2) / sizeof(array2[0]); j++) {
            if (array1[i] == array2[j]) {
                intersecao[contadorArray] = array1[i];
                contadorArray++;
                break;
            }
        }
    }

    // Imprimir a interseção
    printf("A interseção dos arrays é: ");
    for (int i = 0; i < tamanhoIntersecao; i++) {
        printf("%d ", intersecao[i]);
    }

    return 0;
}

